const diplayCountDown = document.getElementById("display-countdown"),
  displayDays = diplayCountDown.querySelector(".days"),
  displayHours = diplayCountDown.querySelector(".hours"),
  displayMinutes = diplayCountDown.querySelector(".minutes");
let birthday = new Date("nov 4, 2023 23:59:00").getTime();
const contentText = document.querySelectorAll(".content"),
  iconBtn = document.querySelectorAll(".icon"),
  picture = document.querySelector(".info-pic"),
  delay = setInterval(() => {
    let t = new Date().getTime(),
      e = birthday - t,
      o = Math.floor(e / 864e5),
      n = Math.floor((e % 864e5) / 36e5),
      i = Math.floor((e % 36e5) / 6e4),
      a = 0 === o || 1 === o ? `${o} jour` : `${o} jours`,
      l = 0 === n || 1 === n ? `${n} heure` : `${n} heures`,
      s = 0 === i || 1 === i ? `${i} minute` : `${i} minutes`;
    (displayDays.textContent = `${a}`),
      (displayHours.textContent = `${l}`),
      (displayMinutes.textContent = `${s}`),
      e < 0 &&
        (clearInterval(delay),
        (diplayCountDown.textContent = "Happy Birthday Denis"));
  }, 1e3);
function showInfos(t) {
  iconBtn.forEach((t) => {
    t.addEventListener("click", (e) => {
      let o = e.target.dataset.icon,
        n = document.querySelectorAll(".content:not(." + o + ")");
      n.forEach((t) => {
        t.classList.add("hide");
      }),
        e.target.classList.toggle("active");
      let i = document.querySelector("." + o);
      i.classList.toggle("hide"),
        document.addEventListener("click", (e) => {
          e.target !== i &&
            e.target !== t &&
            e.target !== picture &&
            (i.classList.add("hide"), t.classList.remove("active")),
            e.stopPropagation();
        });
    });
  });
}
showInfos(),
  window.addEventListener("orientationchange", () => {
    (isLandscape = 90 === window.orientation || -90 === window.orientation)
      ? (diplayCountDown.style.display = "none")
      : (diplayCountDown.style.display = "block");
  });
