document.addEventListener("DOMContentLoaded", (e) => {
  let t = document.querySelectorAll(".icon"),
    o = document.querySelectorAll(".fa-xmark"),
    l = document.querySelectorAll(".nav-item"),
    n = document.querySelector(".container"),
    s = n.querySelectorAll(".content"),
    r = document.querySelector(".invite"),
    c = document.getElementById("display-countdown"),
    i = c.querySelector(".days"),
    a = c.querySelector(".hours"),
    $ = c.querySelector(".minutes"),
    d = new Date("nov 4, 2023 23:59:00").getTime(),
    u = setInterval(() => {
      let e = new Date().getTime(),
        t = d - e,
        o = Math.floor(t / 864e5),
        l = Math.floor((t % 864e5) / 36e5),
        n = Math.floor((t % 36e5) / 6e4),
        s = 0 === o || 1 === o ? `${o} jour` : `${o} jours`,
        r = 0 === l || 1 === l ? `${l} heure` : `${l} heures`,
        f = 0 === n || 1 === n ? `${n} minute` : `${n} minutes`;
      (i.textContent = `${s}`),
        (a.textContent = `${r}`),
        ($.textContent = `${f}`),
        t < 0 && (clearInterval(u), (c.textContent = "Happy Birthday Denis"));
    }, 1e3);
  l.forEach((e) => {
    e.addEventListener("click", (t) => {
      let o = document.querySelectorAll(".active"),
        l = t.target.dataset.icon,
        n = document.querySelectorAll(".content:not(." + l + ")");
      if (
        (n.forEach((e) => e.classList.add("hide")),
        o.forEach((e) => (e ? e.classList.remove("active") : null)),
        t.target.classList.contains("nav-item") ||
          t.target.classList.contains("icon") ||
          t.target.classList.contains("nav-item_text"))
      )
        for (let r = 0; r < e.children.length; r++)
          e.children[r].classList.add("active");
      let c = document.querySelector("." + l);
      if (
        (c.classList.toggle("hide"),
        s.forEach((e) => {
          e.classList.add("fadein");
        }),
        c.classList.contains("hide"))
      ) {
        for (let i = 0; i < e.children.length; i++)
          e.children[i].classList.remove("active");
        s.forEach((e) => {
          e.classList.remove("fadein");
        });
      }
    });
  });
  let f = r.getBoundingClientRect(),
    h = Math.floor(f.top) + 50;
  c.style.setProperty("top", h + "px"),
    o.forEach((e) => {
      e.addEventListener("click", (o) => {
        e.closest(".content").classList.add("hide"),
          e.closest(".content").classList.remove("fadein"),
          t.forEach((e) => {
            e.classList.remove("active"),
              e.nextElementSibling.classList.remove("active");
          }),
          s.forEach((e) => {
            e.classList.remove("fadein");
          });
      });
    });
}),
  history.scrollRestoration
    ? (history.scrollRestoration = "manual")
    : (window.onbeforeunload = function () {
        window.scrollTo(0, 0);
      });
